INSERT INTO `truewp_fm_templates` VALUES ('Form Manager Default','fm-form-default.php','<?php\n/*\nTemplate Name: Form Manager Default\nTemplate Description: The default dislplay template for forms\nTemplate Type: form\n\noption: $showFormTitle, checkbox\n	label: Show form title:\n	default: checked\noption: $showBorder, checkbox\n	label: Show border:\n	default: checked	\noption: $labelPosition, select\n	label: Label position:\n	description: Labels can be placed to the left or above each field\n	options: \'left\' => \'Left\', \'top\' => \'Top\'\n	default: left\noption: $labelWidth, text\n	label: Label width (in pixels):\n	description: Applies to checkboxes, and when labels are to the left\n	default: 200\n\n	\n//////////////////////////////////////////////////////////////////////////////////////////\n\nBelow are the functions that can be used within a form display template:\n\nfm_form_start(), fm_form_end() - These can be called (not echo\'ed) to open and close the form, respectively.\nfm_form_hidden() - Nonce and other hidden values required for the form; can be omitted if fm_form_end() is used.\nfm_form_the_title() - The form\'s title\n\nThe following can be used in place of the fm_form_start() function:\nfm_form_class() - The default form CSS class\nfm_form_action() - The default form action\nfm_form_ID() - Used for the opening form tag\'s \'name\' and \'id\' attributes.\n\nfm_form_the_submit_btn() - A properly formed submit button\nfm_form_submit_btn_name() - Submit button\'s \'name\' attribute\nfm_form_submit_btn_id() - Submit button\'s \'id\' attribute\nfm_form_submit_btn_text() - Submit button\'s \'value\' attribute, as set in the form editor.\nfm_form_submit_btn_script() - Validation script\n\nfm_form_have_items() - Returns true if there are more items (used to loop through the form items, similar to have_posts() in wordpress themes)\nfm_form_the_item() - Sets up the current item (similar to the_post() in wordpress themes)\nfm_form_the_label() - The current item\'s label\nfm_form_the_input() - The current item\'s input element\nfm_form_the_nickname() - The current item\'s nickname\n\nfm_form_is_separator() - Returns true if the current element is a horizontal line\nfm_form_is_note() - Returns true if the current element is a note\nfm_form_is_required() - Returns true if the current item is set as required\nfm_form_item_type() - The current item\'s type\n\nfm_form_get_item_input($nickname) - get an item\'s input by nickname\nfm_form_get_item_label($nickname) - get an item\'s label by nickname\n\n//////////////////////////////////////////////////////////////////////////////////////////\n\n*/\n\n/* translators: the following are for the options for the default form display template */\n__(\"Show form title:\", \'wordpress-form-manager\');\n__(\"Show border:\", \'wordpress-form-manager\');\n__(\"Label position:\", \'wordpress-form-manager\');\n__(\"Labels can be placed to the left or above each field\", \'wordpress-form-manager\');\n_x(\'Left\', \'template-option\', \'wordpress-form-manager\');\n_x(\'Top\', \'template-option\', \'wordpress-form-manager\');\n__(\"Label width (in pixels):\", \'wordpress-form-manager\');\n__(\"Applies to checkboxes, and when labels are to the left\", \'wordpress-form-manager\');\n\n?>\n<?php echo fm_form_start(); ?>\n\n	<?php if($showBorder): ?><fieldset><?php endif; ?>\n	\n		<?php if($showFormTitle): ?>\n			<?php if($showBorder): ?>\n				<legend><?php echo fm_form_the_title(); ?></legend>\n			<?php else: ?>\n				<h2><?php echo fm_form_the_title(); ?></h2>\n			<?php endif; ?>\n		<?php endif; ?>\n		\n		<ul>\n			<?php while(fm_form_have_items()): fm_form_the_item(); ?>\n			<li id=\"fm-item-<?php echo (fm_form_the_nickname() != \"\" ? fm_form_the_nickname() : fm_form_the_ID()); ?>\">\n				<?php if($labelPosition == \"top\"): ?>\n					<label style=\"display:block;width:<?php echo $labelwidth;?>px;\"><?php echo fm_form_the_label(); ?>\n					<?php if(fm_form_is_required()) echo \"&nbsp;<em>*</em>\"; ?>\n					</label><?php echo fm_form_the_input(); ?>\n				<?php else: ?>\n					<table><tr>\n						<td style=\"width:<?php echo $labelWidth; ?>px\"><label><?php echo fm_form_the_label(); ?><?php if(fm_form_is_required()) echo \"&nbsp;<em>*</em>\"; ?></label></td>\n						<td><?php echo fm_form_the_input(); ?></td>\n					</tr></table>\n				<?php endif; ?>\n			</li>\n			<?php endwhile; ?>\n		</ul>\n\n		<div>\n		 <?php echo fm_form_the_submit_btn(); ?>\n		</div>\n\n	<?php if($showBorder): ?></fieldset><?php endif; ?>\n\n	<?php echo fm_form_hidden(); ?>\n<?php echo fm_form_end(); ?>','',1444735482),('Form Manager Default','fm-summary-default.php','<?php\n/*\nTemplate Name: Form Manager Default\nTemplate Description: The default template for e-mail notifications and summaries\nTemplate Type: email, summary\n\n//////////////////////////////////////////////////////////////////////////////////////////\n\nBelow are the functions that can be used within a summary template:\n\nfm_summary_the_title() - the title of the form\nfm_summary_have_items() - works like have_posts() for wordpress themes.  Returns \'true\' if there are more items left in the form.\nfm_summary_the_item() - works like the_item() for wordpress themes.  Initializes the current form item.\nfm_summary_the_label() - label of the current form item\nfm_summary_the_type() - type of the current form item (text, textarea, checkbox, custom_list, note, separator, recaptcha, file)\nfm_summary_has_data() - returns true if the form element is a data element (as opposed to a separator, note, etc.)\nfm_summary_the_value() - submitted value of the current form item\nfm_summary_the_timestamp() - timestamp for the current submission\nfm_summary_the_user() - the login name for the current user.  If no user is logged in, this returns an empty string.\nfm_summary_the_IP() - the IP address of the user who submitted the form.\nfm_summary_the_nickname() - the current item\'s nickname\nfm_summary_the_parent() - post ID where the form was submitted from\n\nfm_summary_get_item_label($nickname) - get an item\'s label by nickname\nfm_summary_get_item_value($nickname) - get an item\'s value by nickname\n\n*NOTE: \'Summary\' templates can also be used for e-mails.  Notice that under \'Template Type\', both \'email\' and \'summary\' are listed.  If you want to make a template for e-mail notifications only, then you should only put \'email\' under \'Template Type\'.\n\n//////////////////////////////////////////////////////////////////////////////////////////\n\n*/\n?>\n<?php /* The title of the form */ ?>\n<h2><?php echo fm_summary_the_title(); ?></h2>\n\n<?php /* The user\'s first and last name, if there is a logged in user */ ?>\n<?php \n$userName = fm_summary_the_user(); \nif(trim($userName) != \"\"){\n	$userData = get_userdatabylogin($userName);\n	echo \"Submitted by: <strong>\".$userData->last_name.\", \".$userData->first_name.\"</strong><br />\";\n}\n?>\n\n<?php /* The time and date of the submission.  Look up date() in the PHP reference at php.net for more info on how to format timestamps. */ ?>\nOn: <strong><?php echo date(\"M j, Y @ g:i A\", strtotime(fm_summary_the_timestamp())); ?></strong> <br />\nIP: <strong><?php echo fm_summary_the_IP(); ?></strong> <br />\n<?php /* The code below displays each form element, in order, along with the submitted data. */ ?>\n<ul id=\"fm-summary\">\n<?php while(fm_summary_have_items()): fm_summary_the_item(); ?>\n	<?php switch(fm_summary_the_type()){\n		case \'separator\': ?><hr /><?php break;\n		default:\n		if(fm_summary_has_data()): ?>\n		<li<?php if(fm_summary_the_nickname() != \"\") echo \" id=\\\"fm-item-\".fm_summary_the_nickname().\"\\\"\";?>><?php echo fm_summary_the_label();?>: <strong><?php echo fm_summary_the_value();?></strong></li>\n	<?php endif;\n		} // end switch ?>\n<?php endwhile; ?>\n</ul>','',1444735482),('Summary for Data List','fm-summary-multi.php','<?php\n/*\nTemplate Name: Summary for Data List\nTemplate Description: This is used by for displaying the submission data as a list of summaries, for example the [formdata] shortcode\nTemplate Type: email, summary\n\n//////////////////////////////////////////////////////////////////////////////////////////\n\n*/\n?>\n<?php /* The user\'s first and last name, if there is a logged in user */ ?>\n<?php \n$userName = fm_summary_the_user(); \nif($userName != \"\"){\n	$userData = get_userdatabylogin($userName);\n	echo \"Submitted by: <strong>\".$userData->last_name.\", \".$userData->first_name.\"</strong><br />\";\n}\n?>\n\n<?php /* The time and date of the submission.  Look up date() in the PHP reference at php.net for more info on how to format timestamps. */ ?>\nOn: <strong><?php echo date(\"M j, Y @ g:i A\", strtotime(fm_summary_the_timestamp())); ?></strong> <br />\nIP: <strong><?php echo fm_summary_the_IP(); ?></strong> <br />\n<?php /* The code below displays each form element, in order, along with the submitted data. */ ?>\n<ul id=\"fm-summary-multi\">\n<?php while(fm_summary_have_items()): fm_summary_the_item(); ?>\n	<?php if(fm_summary_the_type() == \'separator\'): ?>\n		<hr />\n	<?php elseif(fm_summary_has_data()): ?>\n		<li<?php if(fm_summary_the_nickname() != \"\") echo \" id=\\\"fm-item-\".fm_summary_the_nickname().\"\\\"\";?>><?php echo fm_summary_the_label();?>: <strong><?php echo fm_summary_the_value();?></strong></li>\n	<?php endif; ?>\n<?php endwhile; ?>\n</ul>\n<hr />','',1444735482);
